import React, { createContext, useContext, useState } from "react";

// Criação do contexto
const GameContext = createContext();

// Função para acessar o contexto
export const useGameContext = () => {
  return useContext(GameContext);
};

// Provider para envolver a aplicação
export function GameProvider({ children }) {
  const [currentLetters, setCurrentLetters] = useState(["Q", "A", "T"]);
  
  // Função para atualizar as letras
  const changeLetter = (index, letter) => {
    const newLetters = [...currentLetters];
    newLetters[index] = letter;
    setCurrentLetters(newLetters);
  };

  return (
    <GameContext.Provider value={{ currentLetters, setCurrentLetters, changeLetter }}>
      {children}
    </GameContext.Provider>
  );
}
