import React from "react";
import { useGameContext } from "../context/GameContext";

export function Keyboard({ selectedIndex, setSelectedIndex }) {
  const { currentLetters, updateLetters } = useGameContext();

  const handleKeyPress = (letter) => {
    updateLetters(letter.toUpperCase(), selectedIndex);
  };

  const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split("");

  return (
    <div className="keyboard">
      {alphabet.map((letter) => (
        <button
          key={letter}
          onClick={() => handleKeyPress(letter)}
          className="keyboard-button"
        >
          {letter}
        </button>
      ))}
      <div className="navigation-buttons">
        <button onClick={() => setSelectedIndex((prev) => (prev - 1 + currentLetters.length) % currentLetters.length)}>
          ←
        </button>
        <button onClick={() => setSelectedIndex((prev) => (prev + 1) % currentLetters.length)}>
          →
        </button>
      </div>
    </div>
  );
}
