import React from "react";
import { useGameContext } from "./GameContext";

export function WordChecker({ validateWord }) {
  const { currentLetters, addUsedLetters } = useGameContext();

  const handleCheck = () => {
    const word = currentLetters.join("");
    if (validateWord(word)) {
      addUsedLetters(currentLetters);
    } else {
      alert("Palavra inválida!");
    }
  };

  return <button onClick={handleCheck}>Verificar Palavra</button>;
}
