import React from "react";
import { useGameContext } from "./GameContext"; // Certifique-se de que a importação está correta

export function InputField() {
  const { currentLetters, changeLetter } = useGameContext(); // Acesso ao estado e função

  const handleLetterChange = (index, letter) => {
    changeLetter(index, letter); // Atualiza a letra na posição específica
  };

  return (
    <div>
      {currentLetters.map((letter, index) => (
        <input
          key={index}
          type="text"
          value={letter}
          onChange={(e) => handleLetterChange(index, e.target.value)}
          maxLength={1}
        />
      ))}
    </div>
  );
}
